import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const labels = ["Toxic", "Severe Toxic", "Obscene", "Threat", "Insult", "Identity Hate"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const response = await axios.post("http://127.0.0.1:8000/predict/", { text });
      setPrediction(response.data.prediction);
    } catch (error) {
      console.error("Prediction error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">üí¨ Toxic Comment Detector</h1>
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-2xl shadow-lg w-full max-w-lg space-y-4"
      >
  <textarea
    rows="5"
    value={text}
    onChange={(e) => setText(e.target.value)}
    placeholder="Type a comment..."
    className="w-full p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-400 focus:outline-none"
    onKeyDown={(e) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault(); // Prevent newline
        handleSubmit(e);    // Trigger submit
      }
    }}
  />
        <button
          type="submit"
          disabled={isLoading}
          className={`w-full flex justify-center items-center p-3 rounded-lg text-white font-semibold ${
            isLoading ? "bg-blue-300 cursor-not-allowed" : "bg-blue-500 hover:bg-blue-600"
          }`}
        >
          {isLoading ? (
            <>
              <svg
                className="animate-spin h-5 w-5 mr-3 text-white"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                />
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
                />
              </svg>
              Analyzing...
            </>
          ) : (
            "Analyze"
          )}
        </button>
      </form>

      {prediction && (
        <div className="bg-white p-6 rounded-2xl shadow-lg w-full max-w-lg mt-6">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">üìù Results:</h2>
          <ul className="space-y-2">
            {labels.map((label, index) => (
              <li
                key={index}
                className={`flex justify-between p-3 rounded-lg ${
                  prediction[index] >= 0.5 ? "bg-red-100 text-red-600" : "bg-green-100 text-green-600"
                }`}
              >
                {label}:{" "}
                <span>{prediction[index] >= 0.5 ? "‚ö†Ô∏è Yes" : "‚úÖ No"}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
