import React, { useState } from "react";
import axios from "axios";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Loader } from "lucide-react";

function App() {
  const [text, setText] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [totals, setTotals] = useState(null);

  const labels = [
    "Toxic",
    "Severe Toxic",
    "Obscene",
    "Threat",
    "Insult",
    "Identity Hate",
  ];

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => setText(event.target.result);
      reader.readAsText(file);
    }
  };

  const processInputs = (input) => {
    return input
      .split(/\n|,|\|/)
      .map((line) => line.trim())
      .filter((line) => line.length > 0);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPrediction(null);
    setTotals(null);

    const inputs = processInputs(text);
    let totalCounts = Array(labels.length).fill(0);

    try {
      const results = await Promise.all(
        inputs.map((input) =>
          axios.post("http://127.0.0.1:8000/predict/", { text: input })
        )
      );

      const preds = results.map((res) => res.data.prediction);
      preds.forEach((pred) => {
        pred.forEach((value, idx) => {
          if (value >= 0.5) totalCounts[idx] += 1;
        });
      });

      setPrediction(preds);
      setTotals(totalCounts);
    } catch (error) {
      console.error("Prediction error:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">
        Toxic Comment Detector ðŸ’¬
      </h1>
      <Card className="w-full max-w-3xl shadow-2xl p-6">
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <textarea
              rows="6"
              className="w-full border rounded-2xl p-3 text-base"
              value={text}
              onChange={(e) => setText(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Type comments here or paste multiple separated by commas, newlines, or '|'..."
            />
            <input
              type="file"
              accept=".txt"
              className="block w-full text-sm text-gray-500"
              onChange={handleFileUpload}
            />
            <Button type="submit" className="w-full py-2 rounded-2xl text-white bg-blue-600">
              {loading ? (
                <Loader className="animate-spin w-5 h-5 mx-auto" />
              ) : (
                "Analyze"
              )}
            </Button>
          </form>

          {totals && (
            <div className="mt-8 bg-white p-4 rounded-xl shadow">
              <h2 className="text-xl font-semibold mb-4">Toxicity Summary</h2>
              <ul className="grid grid-cols-2 gap-4">
                {labels.map((label, idx) => (
                  <li key={idx} className="text-base">
                    {label}: <span className="font-bold">{totals[idx]}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
