import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [prediction, setPrediction] = useState(null);

  const labels = ["Toxic", "Severe Toxic", "Obscene", "Threat", "Insult", "Identity Hate"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/predict/", { text });
      setPrediction(response.data.prediction);
    } catch (error) {
      console.error("Prediction error:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 to-purple-100 flex items-center justify-center">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-lg">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
          Toxic Comment Detector üí¨
        </h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <textarea
            rows="5"
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Type a comment..."
            className="w-full p-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-lg transition-all duration-200"
          >
            üîç Analyze
          </button>
        </form>

        {prediction && (
          <div className="mt-6">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">üìù Results:</h2>
            <ul className="space-y-2">
              {labels.map((label, index) => (
                <li
                  key={index}
                  className={`p-3 rounded-lg ${
                    prediction[index] >= 0.5
                      ? "bg-red-100 text-red-600 font-semibold"
                      : "bg-green-100 text-green-600 font-semibold"
                  }`}
                >
                  {label}: {prediction[index] >= 0.5 ? "‚ö†Ô∏è Yes" : "‚úÖ No"}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
