import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [text, setText] = useState("");
  const [prediction, setPrediction] = useState(null);

  const labels = ["Toxic", "Severe Toxic", "Obscene", "Threat", "Insult", "Identity Hate"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/predict/", { text });
      setPrediction(response.data.prediction);
    } catch (error) {
      console.error("Prediction error:", error);
    }
  };

  return (
    <div className="App">
      <h1>Toxic Comment Detector üí¨</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          rows="5"
          cols="50"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a comment..."
        />
        <button type="submit">Analyze</button>
      </form>

      {prediction && (
        <div className="results">
          <h2>Results:</h2>
          <ul>
            {labels.map((label, index) => (
              <li key={index}>
                {label}: {prediction[index] >= 0.5 ? "‚ö†Ô∏è Yes" : "‚úÖ No"}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
